PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE productos (
    id          INTEGER PRIMARY KEY,
    nombre      TEXT    NOT NULL,
    url         TEXT    NOT NULL,
    imagen      TEXT    NOT NULL,
    id_categoria    INTEGER REFERENCES categorias (id),
    descripcion_reducida TEXT,
    descripcion TEXT,
    Destacado   BOOLEAN,
    precio      REAL,
    cantidad    INTEGER
);
CREATE TABLE categorias (id INTEGER PRIMARY KEY, nombre TEXT NOT NULL, url TEXT NOT NULL, imagen TEXT NOT NULL, id_padre INTEGER REFERENCES categorias (id), descripcion TEXT, Destacado BOOLEAN, nombre_corto TEXT);
COMMIT;
